mapper.select:
SELECT LIKES.*
FROM LIKES
WHERE CAMPID = #{campid}

SELECT * FROM
(SELECT ROWNUM RN, t.* FROM
(SELECT * FROM
REVIEWS ORDER BY WRITED DESC) t
) WHERE RN BETWEEN #{a} AND
#{b}

select
count(*) from LIKES WHERE LIKER = #{liker} AND CAMPNAME = #{campname}
AND CAMPID = #{campid}

mapper.update:
UPDATE REVIEWS
SET TITLE =
#{title},
IMG = #{image},
POSTBODY=#{postBody}
WHERE POSTNUM=#{postNum}

mapper.select:
SELECT * FROM CAMPREVIEWS
WHERE CAMPID=#{campId}
ORDER BY REVIEWNUM DESC
