select:
SELECT LIKES.*
FROM LIKES 
WHERE CAMPNAME = #{campname }

SELECT * FROM
(SELECT ROWNUM RN, t.* FROM
	(SELECT * FROM REVIEWS ORDER BY WRITED DESC) t
) WHERE RN BETWEEN #{a} AND #{b}

mapper.update:
UPDATE REVIEWS
	SET	 TITLE = ${title}
	 	 IMG = ${image}
	 	 POSTBODY=${postBody} 		
WHERE WRITER=#{writer}
